<template>  <section class="cart">    <div class="container cart__container">      <router-link class="section-title" :to="{name:'catalog'}">       <div class="button button_type_secondary button_width_inner">Вернуться в каталог</div>      </router-link>      <div class="cart__list">        <p v-if="!cartData.length">Товаров в корзине нет</p>        <CartProduct            v-for="(product, index) in cartData"            :key="product.id"            :cart-item-data="product"            @delete-from-cart="deleteFromCart(index)"        />      </div>      <div class="cart__sidebar">        <button class="button button_type_primary" @click="handleOrderComplete">Перейти к оформлению</button>        <h3 class="cart__title-sidebar">Ваша корзина <span class="cart__total-quantity">{{ cartTotalCount }}</span></h3>        <p class="cart__total">Общая стоимость <span class="cart__total-amount">${{ cartTotalCost }}</span></p>      </div>    </div>  </section></template><script>import CartProduct from "../cartProduct/CartProduct";import {mapActions} from "vuex";export default {  name: "Cart",  components: {CartProduct},  props: {    cartData: {      type: Array,      default: () => [],    }  },  computed: {    cartTotalCost(){      return Math.round(this.cartData.reduce((acc, item) => {        return acc + (item.price*item.quantity);      },0))    },    cartTotalCount() {      const count = this.cartData.reduce((acc, item) => {        return acc + item.quantity;      },0)      const countWord =  this.getNoun(count, 'товар', 'товара', 'товаров')      return `${count} ${countWord}`;    },  },  methods: {    ...mapActions([      'DELETE_FROM_CART'    ]),    handleOrderComplete(){      alert(JSON.stringify(this.cartData, null, 4));    },    deleteFromCart(index) {      this.DELETE_FROM_CART(index)    },    getNoun(number, one, two, five) {      let n = Math.abs(number);      n %= 100;      if (n >= 5 && n <= 20) {        return five;      }      n %= 10;      if (n === 1) {        return one;      }      if (n >= 2 && n <= 4) {        return two;      }      return five;    }  }}</script><style scoped>.section-title {  display: block;  font-size: 20px;  line-height: 1.43;  text-align: center;  width: 100%;  margin-bottom: 40px;  text-align: center;}.cart {  padding: 40px 0;  background-color: #ffffff;  height: 100%;}.cart__container {  display: flex;  flex-wrap: wrap;  column-gap: 40px;  align-items: flex-start;}.cart__list {  display: grid;  gap: 20px;  flex: 1;  background-color: #ffffff;  padding: 40px;}.cart__sidebar {  background-color: #ffffff;  padding: 40px;  width: 350px;}.cart-item__price {  margin-left: auto;  margin-right: 50px;}.button {  width: 100%;  border: none;  padding: 10px 40px;  border-radius: 5px;}.button_type_primary {  background: #4faa51;  color: #fff;}.button_type_secondary {  border: 1px solid #4faa51;}.button_width_inner {  width: max-content;  display: inline-block;}.cart__total-quantity {  font-size: 14px;  font-weight: normal;  margin-left: auto;}.cart__title-sidebar {  font-size: 18px;  display: flex;}.cart__total {  font-size: 18px;  font-weight: bold;  display: flex;  padding-top: 20px;  border-top: 1px solid #dadde0;}.cart__total-amount {  margin-left: auto;}</style>