<template>  <div class="cart-item">    <img class="cart-item__img" src="~@/assets/images/catalog/rabbit/rabbit-desktop-1x.jpg" alt="Три корзинки">    <div class="cart-item__description">      <div class="cart-item__info">        <span class="cart-item__heading">{{cartItemData['product_name']}}</span>        <span class="cart-item__characteristics">{{cartItemData.color}}, {{cartItemData.material}}</span>      </div>      <div class="cart-item__price">        <div class="cart-item__input-counter">          <span class="minus" @click="decrementItem">-</span>          <input class="cart-item__couner" type="text" v-model="cartItemData.quantity" readonly/>          <span class="plus" @click="incrementItem">+</span>        </div>        <span class="cart-item__cost">${{cartItemData.price}}</span>      </div>      <div class="cart-item__action">        <button class="cart-item__remove" @click="deleteFromCart">          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 482 482" ><path d="M381.163 57.799h-75.094C302.323 25.316 274.686 0 241.214 0c-33.471 0-61.104 25.315-64.85 57.799h-75.098c-30.39 0-55.111 24.728-55.111 55.117v2.828c0 23.223 14.46 43.1 34.83 51.199v260.369c0 30.39 24.724 55.117 55.112 55.117h210.236c30.389 0 55.111-24.729 55.111-55.117V166.944c20.369-8.1 34.83-27.977 34.83-51.199v-2.828c0-30.39-24.723-55.118-55.111-55.118zm-139.949-31.66c19.037 0 34.927 13.645 38.443 31.66h-76.879c3.515-18.016 19.406-31.66 38.436-31.66zm134.091 401.173c0 15.978-13 28.979-28.973 28.979H136.096c-15.973 0-28.973-13.002-28.973-28.979V170.861h268.182v256.451zm34.83-311.568c0 15.978-13 28.979-28.973 28.979H101.266c-15.973 0-28.973-13.001-28.973-28.979v-2.828c0-15.978 13-28.979 28.973-28.979h279.897c15.973 0 28.973 13.001 28.973 28.979v2.828z"/><path d="M171.144 422.863c7.218 0 13.069-5.853 13.069-13.068V262.641c0-7.216-5.852-13.07-13.069-13.07-7.217 0-13.069 5.854-13.069 13.07v147.154c-.001 7.217 5.851 13.068 13.069 13.068zM241.214 422.863c7.218 0 13.07-5.853 13.07-13.068V262.641c0-7.216-5.854-13.07-13.07-13.07-7.217 0-13.069 5.854-13.069 13.07v147.154c0 7.217 5.851 13.068 13.069 13.068zM311.284 422.863c7.217 0 13.068-5.853 13.068-13.068V262.641c0-7.216-5.852-13.07-13.068-13.07-7.219 0-13.07 5.854-13.07 13.07v147.154c-.001 7.217 5.853 13.068 13.07 13.068z"/></svg>        </button>      </div>    </div>  </div></template><script>import {mapActions} from "vuex";export default {  name: "CartProduct",  props: {    cartItemData: {      type: Object,      default: () => {},    }  },  methods: {    deleteFromCart() {      this.$emit('delete-from-cart')    },    ...mapActions([        'INCREMENT_CART_ITEM',        'DECREMENT_CART_ITEM'    ]),    decrementItem() {      this.DECREMENT_CART_ITEM(this.cartItemData.id)    },    incrementItem() {      this.INCREMENT_CART_ITEM(this.cartItemData.id)    }  }}</script><style scoped>.cart-item {  display: flex;  align-items: center;  width: 100%;}.cart-item__description {  display: flex;  align-items: center;  flex: 1;  justify-content: space-between;}.cart-item__img {  width: 150px;  height: 150px;  object-fit: cover;  margin-right: 40px;  border-radius: 10px;}.cart-item__heading {  font-size: 20px;  font-weight: bold;}.cart-item__price {  margin-left: auto;  margin-right: 50px;}.cart-item__info {  display: flex;  flex-direction: column;}.cart-item__remove {  background: none;  border: none;  cursor: pointer;}.cart-item__cost {  font-size: 18px;  font-weight: bold;}.cart-item__input-counter{  display: flex;  margin-bottom: 10px;}.minus, .plus{  width:40px;  background:#f2f2f2;  border-radius:4px;  border:1px solid #ddd;  text-align: center;  cursor: pointer;}.cart-item__couner {  width: 50px;  text-align: center;  font-size: 18px;  border: 1px solid #ddd;  border-radius: 4px;  display: inline-block;  vertical-align: middle;}</style>