<template>  <section class="products">    <div class="container">      <h2 class="visually-hidden">Список товаров</h2>      <ProductList          :products-data="PRODUCTS"      />    </div>  </section></template><script>import ProductList from "../productList/ProductList";import {mapActions, mapGetters} from "vuex";export default {  name: "CatalogPage",  components: {ProductList},  computed: {  ...mapGetters([      'PRODUCTS'    ])  },  methods:{    ...mapActions([      'GET_PRODUCTS_FROM_API',    ]),  },  mounted() {    this.GET_PRODUCTS_FROM_API()  },}</script><style scoped>  .products {    padding-top: 131px;    margin-bottom: 57px;  }</style>