<template>  <section class="favorites products">    <div class="container favorites__container">      <router-link class="section-title" :to="{name:'catalog'}">        <div class="button button_type_secondary button_width_inner">Вернуться в каталог</div>      </router-link>      <ul class="products__list">        <Product            v-for="product in FAVORITES"            :key="product.id"            :product-data="product"            @add-to-cart="addToCart"            @add-to-favorites="addToFavorites"        />      </ul>    </div>  </section></template><script>import Product from "../product/Product";import {mapActions, mapGetters} from "vuex";export default {  name: "FavoritesList",  components: {Product},  computed: {    ...mapGetters([      'FAVORITES'    ])  },  methods: {    ...mapActions([      'GET_PRODUCTS_FROM_API',      'ADD_TO_CART',      'ADD_TO_FAVORITES'    ]),    addToCart(product) {      this.ADD_TO_CART(product)    },    addToFavorites(product){      this.ADD_TO_FAVORITES(product)    }  },}</script><style scoped>.favorites {  padding: 40px 0;  background-color: #ffffff;  height: 100%;}.products__list {  display: grid;  grid-template-columns: repeat(auto-fill, minmax(316px, 1fr));  flex-wrap: wrap;  justify-content: space-between;  gap: 40px;}</style>