<template>  <section class="products">    <div class="container">      <h2 class="visually-hidden">Список товаров</h2>      <ul class="products__list">        <Product            v-for="product in PRODUCTS"            :key="product.id"            :product-data="product"            @add-to-cart="addToCart"        />        <!--        <div @click.self="reset">-->        <!--          ProductList: {{getTitleNew}}-->        <!--          <button @click="changeTitle">Установить</button>-->        <!--        </div>-->      </ul>    </div>  </section></template><script>import Product from "../product/Product";import { mapActions, mapGetters } from 'vuex';export default {  name: "productList",  data() {    return {    }  },  components: {Product},  computed: {    ...mapGetters([        'PRODUCTS'    ])  },  methods: {    ...mapActions([        'GET_PRODUCTS_FROM_API',        'ADD_TO_CART',    ]),    addToCart(product) {      this.ADD_TO_CART(product)    },  },  mounted() {  this.GET_PRODUCTS_FROM_API()  },}</script><style scoped>.products {  padding-top: 131px;  margin-bottom: 57px;}.products__list {  display: grid;  grid-template-columns: repeat(auto-fill, minmax(316px, 1fr));  flex-wrap: wrap;  justify-content: space-between;  gap: 40px;}</style>