<template>  <section class="products">    <h2 class="visually-hidden">Список товаров</h2>    <ul class="products__list">        <Product/>        <Product/>        <Product/>        <Product/>        <Product/>        <Product/>        <div @click.self="reset">          ProductList: {{getTitleNew}}          <button @click="changeTitle">Установить</button>        </div>    </ul>  </section></template><script>import Product from "../product/Product";export default {  name: "productList",  components: {Product},  computed: {    productList() {      return this.$store.state.productData;    },    getTitleNew() {      return this.$store.getters.getTitleNew;    }  },  methods: {    reset() {      this.$store.dispatch("resetTitleAction")    },    changeTitle() {      this.$store.commit("setTitle", "Новый заголовок");    }  }}</script><style scoped>.products {  padding-top: 131px;  margin-bottom: 57px;}.products__list {  display: grid;  grid-template-columns: repeat(auto-fill, minmax(316px, 1fr));  flex-wrap: wrap;  justify-content: space-between;  gap: 40px;}</style>