<template>      <ul class="products__list">        <Product            v-for="product in productsData"            :key="product.id"            :product-data="product"            @add-to-cart="addToCart"            @add-to-favorites="addToFavorites"        />      </ul></template><script>import Product from "../product/Product";import { mapActions } from 'vuex';export default {  name: "productList",  components: {Product},  props: {    productsData: {      type: Array,      default: () => [],    }  },  methods: {    ...mapActions([        'ADD_TO_CART',        'ADD_TO_FAVORITES'    ]),    addToCart(product) {      this.ADD_TO_CART(product)    },    addToFavorites(product){      this.ADD_TO_FAVORITES(product)    }  },}</script><style scoped>.products__list {  display: grid;  grid-template-columns: repeat(auto-fill, minmax(316px, 1fr));  flex-wrap: wrap;  justify-content: space-between;  gap: 40px;}</style>